package mx.unam.aragon.ico.te.basquetbolistas.controladores;


import mx.unam.aragon.ico.te.basquetbolistas.modelos.Basquetbolista;
import mx.unam.aragon.ico.te.basquetbolistas.servicios.BasquetbolistaService;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.*;

@Controller
@RequestMapping("/jugadores")
public class jugadoresController {


    @Autowired
    private BasquetbolistaService basquetbolistaService;

    @GetMapping("/home/")
    public String home() {
        return "home";
    }

    @GetMapping("/basquetbolista/")
    public String basquetbolista(Model model) {
        Basquetbolista basquetbolista = new Basquetbolista("Stephen Curry", 30, "Golden State Warriors", "Wardell Stephen Curry II es un jugador de baloncesto estadounidense que pertenece a la plantilla de los Golden State Warriors de la NBA. Con 1,88 metros de altura, juega en la posición de base.", "data:image/webp;base64,");
        model.addAttribute("basquetbolista", basquetbolista);
        return "basquetbolista";
    }

    @GetMapping("/nuevo")
    public String nuevo(Model model) {
        model.addAttribute("basquetbolista", new Basquetbolista("LeBron James",23,"Los Angeles Lakers","LeBron Raymone James Sr. es un jugador de baloncesto estadounidense que pertenece a la plantilla de Los Angeles Lakers de la NBA. Con 2,06 metros de estatura, su posición es la de alero, pero su talento, versatilidad y poderío físico le permiten jugar tanto de base como de ala-pívot.","https://encrypted-tbn2.gstatic.com/licensed-image?q=tbn:ANd9GcR9HBABoKNaq79bJoff1E4kq4ZRNku--rv8YtqF3RZY5nX75QM37xQGFGo5NQ8tmRDNxPDfkcp6_gwLfkc"));
        return "formBasquetbolista";
    }

    @PostMapping("/guardar")
    public String guardar(
            @ModelAttribute Basquetbolista basquetbolista
    ){
        LoggerFactory.getLogger(jugadoresController.class).info("Guardando jugador + " + basquetbolista);
        // Mandarlo a BD (save)
        basquetbolistaService.guardarBaquetbolista(basquetbolista);
        return "redirect:/jugadores/nuevo?exito";
    }

    @GetMapping("/basquetbolista/{id}")
    public String basquetbolista(@PathVariable Integer id, Model model){
        model.addAttribute("basquetbolista",basquetbolistaService.getBasquetbolista(id));
        return "basquetbolista";

    }

}